mapper.insert:

	SELECT 'CPT' || LPAD(TO_CHAR(NVL(MAX(to_number(substr(cpt_no,4))), 0)+1), 7, '0')   
	FROM complaint

INSERT INTO complaint (
    cpt_no,
    cpt_comment,
    cpt_title,
    smem_no,
    lec_code,
    cpt_type,
    cpt_code
) VALUES (
	#{cptNo}
    ,#{cptComment}
    ,#{cptTitle}
    ,#{smemNo}
    ,#{lecCode}
    ,#{cptType}
    ,#{cptCode}
)

mapper.update:
UPDATE complaint
SET
   	app_status = #{appStatus}
    ,app_reject = #{appReject}
WHERE
        cpt_no = #{cptNo}
